from flask import Flask ,request , redirect , url_for
import random
from datetime import datetime
import json

app = Flask(__name__)
menuItemsName = {1:"apple",2:"orange",3:"banana"}
counter = {}
tablenumber = "TesttabelA"
randomnumberlist = [0]
orderedMenuItems = {}
menublock = {}


def newitemadder(menuitemname,image):
    
    templateforItem = f"""<p>{menuitemname}-nocount-</p>
    <img src="{image}">
    <p>Type in the amount you want</p>
    <form action="/texted" method="post">
      <input type="number" name="{menuitemname}">
    </form>
    <div style="display:flex; gap:10px;">
    <form action="/+clicked" method="post">
    <button type="submit" value="{menuitemname}" name="plus">+</button>
    </form>
    <form action="/-clicked" method="post">
    <button type="submit" value="{menuitemname}" name="minus">-</button>
    </form>
    </div>"""
   
    menublock[menuitemname] = templateforItem
    counter[menuitemname] = 0

def pageopener():
    global page
    page = ""
    f = open("C:\\Users\\DELL\\Downloads\\Python 2\\restaurant_menu_project\\newmenuproject2\\choosemenupage.html","r")
    page = f.read()
    for block in menublock:
        page += menublock[block]
    for menuname , value in menublock.items():
        page = page.replace(f"{menuname}-nocount-",str(counter[menuname]))
    page += """ <form action="/submit" method="post">
    <button type="submit"  name="data" value= "True">submit</button>
    </form>"""
    f.close()
    return page



newitemadder(menuItemsName[1],"data:image/jpeg;base64,")
newitemadder(menuItemsName[2],"data:image/jpeg;base64,")
newitemadder(menuItemsName[3],"data:image/jpeg;base64,")


@app.route("/")
def index():
    pageopener()
    return page

@app.route(f"/+clicked",methods = ["post"])
def clickedplus():
    form = request.form
    namae = form["plus"]
    if counter[namae] < 10:
        counter[namae] += 1
        
    
    pageopener()
    return page

@app.route(f"/-clicked",methods = ["post"])
def clickedmius():
    form = request.form
    namae = form["minus"]
    if counter[namae] > 0:
        counter[namae] -= 1
    
    pageopener()
    return page

@app.route("/texted",methods = ["post"])
def textt():
    form = request.form
    for key, value in form.items():
        if value == "":
            pass
        else:
            value = int(value)
        
            if value <= 10 and value > 0:
                counter[key] = value
    pageopener()
    return page

