@app.route("/finalpage",methods = ["post"])
def final():

    print(counter)
    num = random.randint(1,10000)
    currenttime = datetime.now().time()
    orderedMenuItems.update({num:{"time":str(currenttime),"order":counter.copy(),"tableNumber":tablenumber}})
    for key, value in counter.items():
        counter[key] = 0
    data = orderedMenuItems
    with open("C:\\Users\\DELL\\Downloads\\Python 2\\restaurant_menu_project\\newmenuproject2\\data.json", "w") as f:
        json.dump(data, f)


    return """<h1>Thank You For Ordering From Us</h1>
    <form action="/newpage" method="post">
    <button type="submit" value="" name="">neworder</button>
    </form>"""

